import{_ as t,c as l,o as i,a0 as o}from"./chunks/framework.zS86-xgI.js";const m=JSON.parse('{"title":"一、VirtualBox：认识与下载安装","description":"","frontmatter":{},"headers":[],"relativePath":"markdown/operations/simulate/virtualbox/01.md","filePath":"markdown/operations/simulate/virtualbox/01.md"}'),r={name:"markdown/operations/simulate/virtualbox/01.md"};function e(n,a,u,s,c,d){return i(),l("div",null,a[0]||(a[0]=[o('<h1 id="一、virtualbox-认识与下载安装" tabindex="-1">一、VirtualBox：认识与下载安装 <a class="header-anchor" href="#一、virtualbox-认识与下载安装" aria-label="Permalink to &quot;一、VirtualBox：认识与下载安装&quot;">​</a></h1><h2 id="_1-1-为什么选virtualbox" tabindex="-1">1.1 为什么选VirtualBox？ <a class="header-anchor" href="#_1-1-为什么选virtualbox" aria-label="Permalink to &quot;1.1 为什么选VirtualBox？&quot;">​</a></h2><ul><li><strong>开源免费</strong>：基于GPLv3协议，个人与企业使用均无成本，且Oracle官方持续维护更新。</li><li><strong>跨平台兼容</strong>：宿主系统覆盖Windows、macOS（含Intel/Arm64/M1/M2）、Linux、Solaris， guest系统支持CentOS、Windows、Ubuntu等数十种。</li><li><strong>轻量高效</strong>：相比VMware，资源占用更低，适合入门级用户与轻量级开发测试场景。</li></ul><h2 id="_1-2-下载virtualbox" tabindex="-1">1.2 下载VirtualBox <a class="header-anchor" href="#_1-2-下载virtualbox" aria-label="Permalink to &quot;1.2 下载VirtualBox&quot;">​</a></h2><ol><li>访问官方下载页：<a href="https://www.oracle.com/cn/virtualization/virtualbox/" target="_blank" rel="noreferrer">https://www.oracle.com/cn/virtualization/virtualbox/</a></li><li>选择对应宿主系统版本： <ul><li>Windows用户：点击“Windows hosts”；</li><li>macOS用户：Intel芯片选“macOS / Intel hosts”，M1/M2芯片选“Developer preview for macOS / Arm64 (M1/M2) hosts”；</li><li>Linux用户：根据发行版（如Ubuntu、CentOS）选择对应包。</li></ul></li><li>版本说明：VirtualBox 7.0为当前主流版本，6.1版本支持至2023年12月，建议优先选7.0（如本文用7.0.8）。</li></ol><h2 id="_1-3-安装virtualbox" tabindex="-1">1.3 安装VirtualBox <a class="header-anchor" href="#_1-3-安装virtualbox" aria-label="Permalink to &quot;1.3 安装VirtualBox&quot;">​</a></h2><ul><li><strong>Windows/macOS</strong>：双击安装包，默认下一步即可。注意： <ul><li>Windows可能弹出“用户账户控制（UAC）”提示，点击“允许”；</li><li>macOS需在“系统设置-安全性与隐私”中允许Oracle扩展程序。</li></ul></li><li><strong>Linux</strong>：通过包管理器安装（如Ubuntu用<code>dpkg -i 包名.deb</code>），过程无特殊配置。</li></ul><p>安装完成后，桌面会出现“Oracle VM VirtualBox”图标，启动后进入管理器界面，准备创建虚拟机。</p>',8)]))}const _=t(r,[["render",e]]);export{m as __pageData,_ as default};
